class vector2 extends Buffer
{
    __New(x, y) 
    {
        super.__New(8, 0)
        NumPut("int", x, "int", y, this.Ptr)
    }
    __Get(Key, Params) 
    {
        if(key == 'x')
        {
            return NumGet(this.Ptr, 0, "int")
        }
        else if(key == 'y')
        {
            return NumGet(this.Ptr, 4, "int")
        }
    }
    __Set(Key, Params, Value) 
    {
        if(key == 'x')
        {
            NumPut("int", Value, this.Ptr)
        }
        else if(key == 'y')
        {
            NumPut("int", Value, this.Ptr, 4)
        }
    }
}



class Structure {
	Size := %this.__Class%.Size
	__New(ptr := unset) => this.Ptr := IsSet(ptr) ? ptr : (this.__Buf := Buffer(this.Size)).Ptr
}

class _POINT extends Structure {
	static Size := 8
	x{
		get => NumGet(this, "int")
		set => NumPut("int", Value, this)
	}
	y{
		get => NumGet(this, 4, "int")
		set => NumPut("int", Value, this, 4)
	}
}
class _POINT extends Buffer {
	__New() => super.__New(8)
	x{
		get => NumGet(this, "int")
		set => NumPut("int", Value, this)
	}
	y{
		get => NumGet(this, 4, "int")
		set => NumPut("int", Value, this, 4)
	}
}

